import React from "react";
import axios from "axios";
import { faPen, faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useEffect } from "react";
import { BASE_URL } from "../conf/environnement";
function Groups(props) {

    const [post, setPost] = React.useState([]);

    React.useEffect(() => {
        const fetch = async () => {
            try {
                await axios.get(baseUrl + "/groups").then((response) => {
                    setPost(response.data);
                });
            } catch (e) {
                console.log(e)
            }
        }
        fetch();
    }, []);

    function check(string) {
        axios.delete(baseUrl + "/groups/" + string);
        setTimeout(1000)
        alert("The group with ID " + string + " is being deleted.")
    }
    return post.map(function (a) {
        return (
            <tr>
                <th scope="row">{a.id}</th>
                <td>{a.groupName}</td>
                <td><FontAwesomeIcon icon={faPen} /></td>
                <td><FontAwesomeIcon icon={faTrash} onClick={() => { check(a.id) }} /></td>
            </tr>
        );
    })
}
export default Groups;
