import React from "react";
import axios from "axios";
import { faPen, faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
function Groups() {
    const buttonOpen = useRef(null);
    const modalElement = useRef(null);
    const [values, setValues] = useState(null);
    const baseUrl = "http://localhost:8080";
    const [post, setPost] = React.useState([]);

    React.useEffect(() => {
        axios.get(baseUrl + "/groups").then((response) => {
            setPost(response.data);
        });
    }, post);

    function check(string){
        axios.delete(baseUrl + "/groups/"+string);
        setTimeout(1000)
        alert("The group with ID "+ string +"is being deleted.")
    }
    function putGroup(e) {
        e.preventDefault();
        setValues(e.target.querySelector("input").value);
        modalElement.current.style.display = 'none';
        axios.post(baseUrl + '/groups',{
            groupName: values
        })
            .then(
            response => response.data
        )
            .catch(err => console.warn(err));
    }
    return post.map(function(a){
        return(
        <tr>
                <th scope="row">{a.id}</th>
                <td>{a.groupName}</td>
                <td><FontAwesomeIcon icon={faPen} onClick={() => {modify(a.id,a.groupName)}}/></td>
                <td><FontAwesomeIcon icon={faTrash} onClick={() => {check(a.id)}}/></td>
            </tr>
        );
    })
}
export default Groups;
