import React from "react";
import Products from "./Products";
import LeftNavbar from "./LeftNavbar";
import { useRef, useEffect, useState } from "react";
import NavBar from "./Navbar";
import ProductModal from "./ProductModal";
import axios from "axios";
import { BASE_URL } from "../conf/environnement";
function TableProducts() {
    const buttonOpen = useRef(null);
    const modalElement = useRef(null);

    function addProduct(e) {
        e.preventDefault();
        modalElement.current.style.display = 'none';
        axios.post(baseUrl + '/products', {
            productName: e.target.querySelector("#exampleInputName").value
        })
        .then(
            response => response.data
        )
        .catch(err => console.warn(err));
    }
    return (
        <div>
            <NavBar />
            <div className="modalDiv" ref={modalElement}>
                <ProductModal addProduct={addProduct} buttonOpen={buttonOpen} modalElement={modalElement}/>
            </div>
            <div className="row">
                <LeftNavbar />
                <div className="col-9">
                    <div className="row ">
                        <h2 className="col-10">Products</h2>
                        <button className="btn btn bg-success text-white" ref={buttonOpen}>Add a Product</button>
                    </div>
                    <table className="table">
                        <thead>
                            <tr>
                                <th scope="col" className="col-5">#</th>
                                <th scope="col" className="col-5">Name</th>
                                <th scope="col" className="col-1">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            <Products></Products>
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}
export default TableProducts;
