import React from "react";
import { useEffect } from "react";
import './assets/styles.css';

function GroupModal(props) {
    function close() {
        props.modalElement.current.style.display = 'none';
    }
    function open() {
        props.modalElement.current.style.display = 'block';
    }

    useEffect(() => {
        props.buttonOpen.current.onclick = open;
    });
    return (
        <div onSubmit={e => props.addGroup(e)} className="col-3 ">
            <h5>Add a new group</h5>
            <form>
                <div className="form-group">
                    <label htmlFor="exampleInputName">Name</label>
                    <input type="text" className="form-control" id="exampleInputName" aria-describedby="nameHelp" placeholder="Enter the name" />
                </div>
                <div className="row">
                    <div className="col-6 text-center">
                        <button type="submit" className="btn btn-primary ">Submit</button>
                    </div>
                    <div className="col-6 text-center">
                        <button type="button" className='btn btn-danger' onClick={close}>Cancel</button>
                    </div></div>
            </form>
        </div>
    );
}
export default GroupModal;
