import React from "react";
import LeftNavbar from "./LeftNavbar";
import Groups from "./Groups";
import NavBar from "./Navbar";
import GroupModal from "./GroupModal";
import { useRef, useEffect, useState } from "react";
import axios from "axios";

function TableGroups() {
    const buttonOpen = useRef(null);
    const modalElement = useRef(null);
    const baseUrl = "http://localhost:8080";

    function addGroup(e) {
        e.preventDefault();
        modalElement.current.style.display = 'none';
       axios.post(baseUrl + '/groups',{
            groupName: e.target.querySelector("#").value
        })
            .then(
            response => response.data
        )
            .catch(err => console.warn(err));
    }
    return (
        <div>
            <NavBar />
            <div className="modalDiv" ref={modalElement}>
                <GroupModal addGroup={addGroup} buttonOpen={buttonOpen} modalElement={modalElement} />
            </div>
            <div className="row">
                <LeftNavbar />
                <div className="col-9">
                    <div className="row">
                        <h2 className="col-10">Groups</h2>
                        <button className="btn btn bg-success text-white" ref={buttonOpen}>Add a Group</button>
                    </div>
                    <table className="table ">
                        <thead>
                            <tr>
                                <th scope="col" className="col-5">#</th>
                                <th scope="col" className="col-5">Name</th>
                                <th scope="col" className="col-1">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            <Groups/>
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}
export default TableGroups;
