import React from "react";
import LeftNavbar from "./LeftNavbar";
import Groups from "./Groups";
import NavBar from "./Navbar";
import GroupModal from "./GroupModal";
import { useRef,useEffect, useState} from "react";

function TableGroups() {
    const buttonOpen = useRef(null);
    const modalElement = useRef(null);


    function addGroup(e) {
        e.preventDefault();
        console.log('Add person !!', e.target.name.value);
        modalElement.current.style.display = 'none';
    }
    return (
        <div>
            <NavBar />
            <div className="modalDiv" ref={modalElement}>
                <GroupModal addGroup={addGroup} buttonOpen={buttonOpen} modalElement={modalElement}></GroupModal>
            </div>
            <div className="row">
                <LeftNavbar />
                <div className="col-9">
                    <div className="row">
                        <h2 className="col-10">Groups</h2>
                        <button className="btn btn bg-success text-white" ref={buttonOpen}>Add a Group</button>
                    </div>
                    <table className="table ">
                        <thead>
                            <tr>
                                <th scope="col">#</th>
                                <th scope="col">Name</th>
                            </tr>
                        </thead>
                        <tbody>
                            <Groups />
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}
export default TableGroups;
